mapper.update:
update transaction_log
invoice_code = #{invoiceCode}, registration_id = #{registrationId}, patient_id = #{patientId}, role_id = #{roleId}, type = #{type}, collection_id = #{collectionId}, project_id = #{projectId}, item_id = #{itemId}, item_name = #{itemName}, amount = #{amount}, pay_type = #{payType}, total_money = #{totalMoney}, status = #{status}, is_frozen = #{isFrozen},
where id = #{id}

mapper.select:
select distinct t.*
from transaction_log t
where invoice_code = #{invoiceCode}

select * from transaction_log t


        t.gmt_create >= #{beginDateStr}


        and t.gmt_create <= #{endDateStr} 

    and registration_id = #{registrationId}
    and (status = 1 or status = 2)

order by t.gmt_modified desc

select invoice_code as invoiceCode, count(invoice_code) as totalInvoiceAmount, max(status) as status, SUM(total_money) as totalMoney, ANY_VALUE(type) as type, ANY_VALUE(pay_type) as payType, ANY_VALUE(registration_id) as registrationId, ANY_VALUE(p.name) as name from transaction_log t join patient p on t.patient_id = p.id
and t.gmt_create >= #{beginDateStr}
and t.gmt_create <= #{endDateStr}
and t.role_id = #{cashierId} and t.is_frozen = 0
group by invoice_code order by invoice_code

select count(invoice_code) from transaction_log t
where t.role_id = #{cashierId} and invoice_code <= #{invoiceCodeEnd} and invoice_code >= #{invoiceCodeBegin}
and is_frozen = 1

select invoice_code from transaction_log t
where t.role_id = #{cashierId} and invoice_code <= #{invoiceCodeEnd} and invoice_code >= #{invoiceCodeBegin}
and status = #{status}

select type, sum(total_money) as total_money FROM transaction_log t
where t.role_id = #{cashierId} and invoice_code <= #{invoiceCodeEnd} and invoice_code >= #{invoiceCodeBegin}
group by type

select r.department_name  as departmentName, SUM(total_money) as totalMoney, t.type, count(distinct invoice_code) as invoiceAmount
from ${tableName} e, role r, transaction_log t
where e.id = t.collection_id and t.project_id = e.${projectIdName} and r.id = e.${roleIdName}
and t.gmt_create <= #{endDateStr} and t.gmt_create >= #{beginDateStr}
group by r.department_name, type
